function clearActive(){for(let t=0;t<document.querySelectorAll(".menu-button").length;t++)document.querySelectorAll(".tab-content")[t].classList.remove("active")}document.querySelector(".menu-button-list").addEventListener("click",(t=>{(t.target.matches(".menu-button")||t.target.matches(".tab-name"))&&clearActive();for(let e=1;e<=document.querySelectorAll(".menu-button").length;e++)t.target.matches(".button"+e)&&document.querySelector(".tab"+e).classList.add("active")}));